import React from 'react';
import { ScrollView, StyleSheet, TextInput, View, Image, TouchableOpacity } from 'react-native';
import { Button, Card } from 'react-native-paper';
import { DefaultTheme } from 'react-native-paper';



function Login ( {navigation} ) {
  return (
    <ScrollView style={styles.scrollView}>
      <Card style={styles.card}>
      <Card.Title title="Login with your UCD email address" />
        <Card.Content>
        <TouchableOpacity style={styles.button} mode="contained" onPress={() => navigation.navigate('SecondScreen')}>
            CAS LOGIN
          </TouchableOpacity>
        </Card.Content>
      </Card>
      <View style={styles.inputView}>
        <TextInput
            style={styles.TextInput}
            placeholder="Email"
            placeholderTextColor="#003f5c"
        />
        </View>
        
        <View style={styles.inputView}>
        <TextInput
            style={styles.TextInput}
            placeholder="Password"
            placeholderTextColor="#003f5c"
            secureTextEntry={true}
        />
        </View>
    </ScrollView>
    
  )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },

    image :{
        marginBottom: 40
    },
    
    TextInput: {
        height: 50,
        flex: 1,
        padding: 10,
        marginLeft: 20,
    },
    button: {
        backgroundColor: "#FFBF1C",
    
    }
     
});

export default Login
